function [yHistory, fitnessHistory, generationCount] = OnePlusOneES(targetFunction, sigma, maxGenerationCount, dimensions)    yParent = rand(1, dimensions); % initialize parental y-vector  generationCount = 0;    % TODO parameter  fitnessParent = targetFunction(yParent);    yHistory = [];  fitnessHistory = [];    do    generationCount    yHistory = [yHistory ; yParent]    fitnessHistory = [fitnessHistory ; fitnessParent];    mutation = stdnormal_rnd(1, dimensions); % normal distributed random number    yOffspring = yParent + sigma * mutation;    fitnessOffspring = targetFunction(yOffspring);    if fitnessOffspring < fitnessParent      yParent = yOffspring;      fitnessParent = fitnessOffspring;    end    generationCount = generationCount + 1;  until generationCount == maxGenerationCount;  yHistory = [yHistory ; yParent];  fitnessHistory = [fitnessHistory ; fitnessParent];end